sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		// If the field is set to Yes then remove Not Applicable as an option
		// for Health authority regulated and Process supported
		if (newValue == '2') {
			g_form.removeOption('u_health_authority_regulated', '0');
			g_form.removeOption('u_process_supported', '0');
		}
		return;
	}

	if (newValue == '1') {
		// Process Supported to N/A
		g_form.addOption('u_process_supported', '0', 'Not Applicable');
		g_form.setValue('u_process_supported', '0');
	}
	else {
		g_form.removeOption('u_process_supported', '0');
		g_form.setValue('u_process_supported', '2');
	}
	// call calcgxpassessment() to set the GXP risk value
	g_form.setValue('u_gxp_assessment', calcgxpassessment());
}
