sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	// What to do on the new value
	switch (newValue) {
		case '3': // Yes
			// Set Health Authority Regulated and Process Supported to N/A
			g_form.setValue('u_ha_regulated_comments', 0);
			g_form.setValue('u_process_supported', 0);
			// Set GXP assessment to High
			g_form.setValue('u_gxp_assessment', 3);
			break;
		case '2': // No

			break;
		case '1': // N/A

			break;
		case '0': // --None--

			break;
	}

}
