sys_variable_value.value:
// To create a new requirement identifier start by concatenating 
// the project identifer plus the requirement type.
var project = current.u_project_identifier.project_id;
var requirement_type = current.u_requirement_type.abbreviation;
var requirement_identifier = project + '-' + requirement_type + '-';

// The select the next requirement number for this project and requirement type and update the
// requirement numbers, or creqte a new counter for this Proejct-Requirement type if
// one doesn't exist yet.

// Build a query to fetch the current counter value for this Project
// and requirement type.
var requirement_counter = new GlideRecord('x_173342_cicsv_t1_requirement_counter');
requirement_counter.addQuery('u_project_identifier.project_id', project);
requirement_counter.addQuery('u_requirement_type.abbreviation', requirement_type);
requirement_counter.query();

// If the query returns a row then update the counter value and use it in the current record;
if (requirement_counter.next()) {
	// If the counter is unset then set it to 1
	if (requirement_counter.u_counter == '' || requirement_counter.u_counter == 0) {
		requirement_counter.u_counter = 1;
	} else {
		// Otherwise increment the counter
		requirement_counter.u_counter++;
	}	
	// Update the counter value in the database
	requirement_counter.update();

}
// Else we must insert a new record
else {
	requirement_counter.u_project_identifier = current.u_project_identifier;
	requirement_counter.u_requirement_type = current.u_requirement_type;
	requirement_counter.u_counter = 1;
	requirement_counter.insert();
}

// Append the number to the requirement identifier field
requirement_identifier += pad(requirement_counter.u_counter, 5);
current.u_requirement_identifier = requirement_identifier;

//
// Pad numbers with leading zeros
//
function pad(num, size) {
    var s = num+"";
    while (s.length < size) s = "0" + s;
    return s;
}

