			// vp.u_validation_summary_report = '10';
			vp.u_trace_matrix = '10';
			vp.u_release_memo = '10';
		}
	} else if (current.u_risk_assessment == '2') {
		// Low Risk Assessment
		if (current.u_gxp_criticality > '2') {
			// GXP
			vp.u_csv_risk_assessment = '10';
			vp.u_21_cfr_part_11 = '10';
